import styles from "./index.module.css";
import { useState, useEffect } from "react";
import RepoCard from "../RepoCard";
import { api } from "../../services/api";

function UserInfoCard({ userDetails, userDetailsLoading, username }) {
  const [repos, setRepos] = useState([]);
  const [repoDetails, setRepoDetails] = useState({});
  const [repoDetailsLoading, setRepoDetailsLoading] = useState(false);

  function searchRepos(username) {
    api.get(`/users/${username}/repos`).then((res) => {
      setRepos(res.data);
    });
  }

  function renderRepo(repo) {
    return (
      <div
        className={styles.repoContainer}
        onClick={() => getRepoDetails(repo.name)}
        key={repo.id}
      >
        <h2 className={styles.repoName}>{repo.name}</h2>
      </div>
    );
  }

  function getRepoDetails(repoName, username) {
    setRepoDetailsLoading(true);
    api.get(`/repos/${username}/${repoName}`).then((res) => {
      setRepoDetailsLoading(false);
      setRepoDetails(res.data);
    });
  }

  if (userDetailsLoading) {
    return <h1 className={styles.loader}>Loading</h1>;
  }

  return (
    <div className={styles.userDetailContainer}>
      <div className={styles.detailsRow}>
        <label className={styles.infoLabel}>Name:</label>
        <span className={styles.value}>{userDetails.name}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>location:</label>
        <span className={styles.value}>{userDetails.location}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>company:</label>
        <span className={styles.value}>
          {" "}
          {userDetails.company ? userDetails.company : "-"}
        </span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>avatar_url:</label>
        <span className={styles.value}> {userDetails.avatar_url}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>public_repos:</label>
        <span className={styles.value}> {userDetails.public_repos}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>followers:</label>
        <span className={styles.value}> {userDetails.followers}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>following:</label>
        <span className={styles.value}> {userDetails.following}</span>
      </div>

      <div className={styles.resultsContainer}>{repos.map(renderRepo)}</div>

    
      <RepoCard
        repoDetails={repoDetails}
        repoDetailsLoading={repoDetailsLoading}
      />
    </div>
  );
}

export default UserInfoCard;
