import styles from "./index.module.css";

function RepoCard({  repoDetailsLoading, repoDetails }) {
  if (repoDetailsLoading) {
    return <h1 className={styles.loader}>Loading</h1>;
  }

  return (
    <div className={styles.repoDetailContainer}>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Repo Name:</label>
        <span className={styles.value}>{repoDetails?.name}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Created at:</label>
        <span className={styles.value}>{repoDetails?.created_at}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>URL:</label>
        <span className={styles.value}> {repoDetails.html_url}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Language:</label>
        <span className={styles.value}> {repoDetails.language}</span>
      </div>
     <button className={styles.favButton}>Favorilere ekle</button>
    </div>
  );
}

export default RepoCard;
