import "./style.css";
import { useStarred } from "../../context/StarredContext";
import { useState } from "react";
const HomeScreen = () => {
  const { repos ,setRepos } = useStarred();

  function linkToGithub () {
    window.location.replace("www.github.com" + element);
  }

  return (
    <div>
      <h1>Starred Repos</h1>
      {repos.map(element => { 
        return (
          <div className="starred-repo-container">
            
              <p className="starred-repo" onClick={linkToGithub}>{element}<br></br></p>
             
            
                 
          </div>
     
          
         );
      })
      }
    </div>
  );
};

export default HomeScreen;
