import styles from "./index.module.css";

function RepoCard({ username, repo, repoDetailsLoading, repoDetails }) {
  if (repoDetailsLoading) {
    return <h1 className={styles.loader}>Loading</h1>;
  }

  function searchRepos() {
    fetch(`https://api.github.com/users/${username}/repos`)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(36, result);
          const list = result.map((item) => (
            <div className={styles.repoInfo}>
              <a target="_blank" href={item.svn_url}>
                {item.name}
              </a>
            </div>
          ));
          setRepoDetails(list);
        },
        (error) => {
          console.log(error);
        }
      );
  }

  
  function renderRepo(repo) {
    return (
      <div
        className={styles.repoContainer}
        onClick={() => getRepoDetails(repo.name)}
        key={repo.id}
      >
        <h2 className={styles.repoName}>{repo.name} sahbahshbahb</h2>
      </div>
    );
  }

  function getRepoDetails(repoName) {
    setRepoDetailsLoading(true);
    fetch(`https://api.github.com/repos/${username}/${repoName}`).then(
      (res) => {
        setRepoDetailsLoading(false);
        setRepoDetails(res.data);
      }
    );
  }


  return (
    <div className={styles.repoDetailContainer}>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Repo Name:</label>
        <span className={styles.value}>{repoDetails?.name}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Created at:</label>
        <span className={styles.value}>{repoDetails?.created_at}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>URL:</label>
        <span className={styles.value}> {repoDetails?.html_url}</span>
      </div>
      <div className={styles.detailsRow}>
        <label className={styles.repoLabel}>Language:</label>
        <span className={styles.value}> {repoDetails?.language}</span>
      </div>
     <button className={styles.favButton}>Favorilere ekle</button>
    </div>
  );
}

export default RepoCard;
