import { useState, useEffect } from "react";

import { api } from "../../services/api";
import styles from "./index.module.css";

import RepoCard from "../RepoCard";
import UserInfoCard from "../UserInfoCard";

const SearchBar = () => {
  const [username, setUsername] = useState("");
  const [loading, setLoading] = useState(false);
  // const [repos, setRepos] = useState([]);
  const [repoDetails, setRepoDetails] = useState({});
  const [repoDetailsLoading, setRepoDetailsLoading] = useState(false);
  const [users, setUsers] = useState([]);
  const [userDetails, setUserDetails] = useState({});
  const [userDetailsLoading, setUserDetailsLoading] = useState(false);

  const [repoData, setRepoData] = useState();

  // useEffect(() => {
  //   setRepos([]);
  //   setRepoDetails({});
  // }, [username]);

  useEffect(() => {
    setUsers([]);
    setUserDetails({});
    setRepoData();
  }, [username]);

  function handleSubmit(e) {
    e.preventDefault();
    searchUsers();
  }

  // function searchRepos() {
  //   setLoading(true);
  //   api.get(`/users/${username}/repos`).then((res) => {
  //     setLoading(false);
  //     setRepos(res.data);
  //   });
  // }

  function searchUsers() {
    setLoading(true);
    api.get(`/users/${username}`).then((res) => {
      setLoading(false);
      setUserDetails(res.data);
      console.log(res.data);
    });
  }

  // function renderRepo(repo) {
  //   return (
  //     <div
  //       className={styles.repoContainer}
  //       onClick={() => getRepoDetails(repo.name)}
  //       key={repo.id}
  //     >
  //       <h2 className={styles.repoName}>{repo.name}</h2>
  //     </div>
  //   );
  // }

  function renderUser(user) {
    return (
      <div
        className={styles.userInfoContainer}
        onClick={() => getUserDetails(user.name)}
        key={user.id}
      >
        <h2 className={styles.username}>{user.name}</h2>
      </div>
    );
  }

  // function getRepoDetails(repoName) {
  //   setRepoDetailsLoading(true);
  //   api.get(`/repos/${username}/${repoName}`).then((res) => {
  //     setRepoDetailsLoading(false);
  //     setRepoDetails(res.data);
  //   });
  // }

  function getUserDetails(username) {
    setUserDetailsLoading(true);
    api.get(`/users/${username}`).then((res) => {
      setUserDetailsLoading(false);
      setUserDetails(res.data);
    });
  }

  async function repoDataURL() {
    //Get repo data about github user
    await fetch(`https://api.github.com/users/${username}/repos`)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(36, result);
          setRepoDetails(result);
          // const list = result.map((item) => (
          //   <div className="text-center">
          //     <a target="_blank" href={item.svn_url}>
          //       {item.name}
          //     </a>
          //   </div>
          // ));
          // setRepoData(list);
        },
        (error) => {
          console.log(error);
        }
      );
  }

  return (
    <div className={styles.searchContainer}>
      <form className={styles.form}>
        <input
          className={styles.input}
          value={username}
          placeholder="Bir kullanıcı adı giriniz"
          onChange={(e) => setUsername(e.target.value)}
        />

        <button className={styles.searchButton} onClick={handleSubmit}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      
    
      <div className={styles.resultsContainer}>{users.map(renderUser)}</div>
      <UserInfoCard
        userDetails={userDetails}
        userDetailsLoading={userDetailsLoading}
      />

      <button className={styles.listRepos} onClick={repoDataURL}>List public repos:</button>  
      <RepoCard repoDetails={repoDetails} repoDetailsLoading={repoDetailsLoading} /> 
      {repoData}
    </div>
  );
};

export default SearchBar;
